INSERT INTO recipes (title, description, ingredients, instructions, cooking_time, difficulty, category_id, image_path) VALUES
('Брускетта с томатами и базиликом', 'Итальянская закуска на поджаренном хлебе', 'Багет - 1 шт, Помидоры - 2 шт, Чеснок - 2 зубчика, Базилик - 10 г, Оливковое масло - 3 ст.л., Бальзамический крем - 1 ч.л., Соль - по вкусу', '1. Поджарьте ломтики багета. 2. Натрите чесноком. 3. Выложите нарезанные помидоры. 4. Посыпьте базиликом, полейте маслом и бальзамиком.', 15, 'легко', 7, 'bruschetta.jpg'),
('Тартар из говядины', 'Французская закуска из сырой говядины', 'Говяжья вырезка - 200 г, Каперсы - 1 ст.л., Лук красный - 0.5 шт, Корнишоны - 2 шт, Яичный желток - 1 шт, Дижонская горчица - 1 ч.л., Ворчестерский соус - 1 ч.л.', '1. Мелко нарежьте охлажденную говядину. 2. Смешайте с каперсами, луком и корнишонами. 3. Добавьте соусы и горчицу. 4. Сформируйте горку, сверху сделайте углубление для желтка.', 20, 'средне', 7, 'beef_tartare.jpg'),
('Девальированные яйца', 'Классическая закуска с разнообразными начинками', 'Яйца - 6 шт, Майонез - 3 ст.л., Горчица - 1 ч.л., Укроп - 1 ст.л., Паприка - 0.5 ч.л., Соль - по вкусу, Перец - по вкусу', '1. Сварите яйца вкрутую. 2. Очистите и разрежьте пополам. 3. Достаньте желтки и смешайте с майонезом, горчицей и специями. 4. Наполните половинки белков полученной смесью.', 25, 'легко', 7, 'deviled_eggs.jpg'),
('Капрезе на шпажках', 'Мини-салат из моцареллы, томатов и базилика', 'Моцарелла мини - 20 шт, Помидоры черри - 20 шт, Базилик свежий - 20 листьев, Оливковое масло - 2 ст.л., Бальзамический крем - 1 ст.л., Соль - по вкусу', '1. Нанижите на шпажки: моцареллу, помидор, лист базилика. 2. Выложите на блюдо. 3. Полейте оливковым маслом и бальзамиком. 4. Посолите по вкусу.', 10, 'легко', 7, 'caprese_skewers.jpg'),
('Рулетики из баклажанов с сыром', 'Закуска из запеченных баклажанов с начинкой', 'Баклажаны - 2 шт, Сыр фета - 150 г, Грецкие орехи - 50 г, Чеснок - 2 зубчика, Укроп - 2 ст.л., Оливковое масло - 3 ст.л.', '1. Нарежьте баклажаны вдоль тонкими пластинами. 2. Запеките до мягкости. 3. Приготовьте начинку из сыра, орехов, чеснока и укропа. 4. Заверните начинку в баклажаны.', 40, 'средне', 7, 'eggplant_rolls.jpg'),
